00066 void Background::update(sf::RenderWindow& window, sf::View& mainView,
00067                         float gameSpeed, sf::Texture* spritesheet,
00068                         std::vector<Obstacle*>& obstacles,
00069                         std::vector<Enemy*>& enemies, Player& player,
00070                         std::vector<Wall*>& walls,
00071                         std::vector<ZapWall*>& zapWalls
00072 )
00073 {
00074     //checks the transition between stages
00075     if (backgroundFinished(mainView))
00076     {
00077         switch (stage)
00078         {
00079         case INITIAL:
00080             stage = SPACE;
00081             back.setTexture(space);
00082             resetPos(mainView, player, 0);
00083             break;
00084         case SPACE:
00085             stage = BOSS;
00086             back.setTexture(boss);
00087             resetPos(mainView, player, 0);
00088             break;
00089         case BOSS:
00090             stage = BOSSFIGHT;
00091         }
00092 
00093         generateObstacles(stage, obstacles, spritesheet, walls, zapWalls);
00094         generateWaves(stage, enemies, spritesheet,
00095                       static_cast<int>(player.getPos().z));
00096     }
00097 
00098     if (!backgroundFinished(mainView))
00099     {
00100         float modifier = 1;
00101         if (stage == SPACE) // space moves 2/3 speed
00102             modifier = 0.66f;
00103         mainView.move(
00104             translateTo2d(sf::Vector3f(0, 0, -1.3f * gameSpeed * modifier)));
00105     }
00106 
00107     // spawn waves that have gone past the z set in queue
00108     if (!waveQueue.empty() && player.getPos().z < waveQueue.front().first)
00109     {
00110         Enemy::spawnWave(enemies, spritesheet,
00111                          static_cast<int>(player.getPos().z),
00112                          waveQueue.front().second);
00113         waveQueue.pop();
00114     }
00115 
00116     window.draw(back);
00117 }
