00188 bool Background::isInSpace(int z)
00189 {
00190     // Initial -2800: space
00191     // Boss -123: No space
00192     bool inSpace = false;
00193     switch (stage)
00194     {
00195     case INITIAL:
00196         if (z > -123 || z < -2800)
00197             inSpace = true;
00198         break;
00199     case SPACE:
00200         inSpace = true;
00201         break;
00202     case BOSS:
00203         if (z > -123)
00204             inSpace = true;
00205     }
00206 
00207     return inSpace;
