00217 void Background::resetPos(sf::View& mainView, Player& player, int startPos)
00218 {
00219     //Sets the origin to the bottom left corner as that is where it will start 
00220     //on the screen
00221     mainView.setCenter(sf::Vector2f(112, 100));
00222     back.setOrigin(
00223         sf::Vector2f(0, static_cast<float>(back.getTexture()->getSize().y)));
00224 
00225     int adder = stage == SPACE ? 350 : 0;
00226     sf::Vector2f moveVector = sf::Vector2f(.8f * (startPos + adder),
00227                                    -.4f * (startPos + adder));
00228     mainView.move(moveVector);
00229 
00230     switch (stage)
00231     {
00232     case SPACE:
00233         back.setPosition(sf::Vector2f(0, 224));
00234         player.resetPos(startPos + 350);
00235         break;
00236     case BOSS:
00237         back.setPosition(sf::Vector2f(0, 238));
00238         player.resetPos(startPos);
00239         break;
00240     default:
00241         back.setPosition(sf::Vector2f(0, 224));
00242         player.resetPos(startPos);
00243     }
00244 }
