00608 void Background::generateWaves(Stage stage,
00609                                std::vector<Enemy*>& enemies,
00610                                sf::Texture* spriteSheet, int playerZ)
00611 {
00612     for (Enemy* enemy : enemies)
00613         delete enemy;
00614     enemies.clear();
00615     // equivalent to waveQueue.clear();
00616     std::queue<std::pair<int, unsigned int>>().swap(waveQueue);
00617 
00618     // format waveQueue.push(std::pair<int, unsigned int>(spawnZ, waveId));
00619     switch (stage)
00620     {
00621     case SPACE: // further below Z should be lesser
00622         waveQueue.push(std::pair<int, unsigned int>(-460, 0));
00623         waveQueue.push(std::pair<int, unsigned int>(-493, 0));
00624         waveQueue.push(std::pair<int, unsigned int>(-526, 0));
