00629         waveQueue.push(std::pair<int, unsigned int>(-991, 4));
00630         waveQueue.push(std::pair<int, unsigned int>(-1051, 4));
00631         waveQueue.push(std::pair<int, unsigned int>(-1084, 4));
00632         waveQueue.push(std::pair<int, unsigned int>(-1104, 5));
00633         waveQueue.push(std::pair<int, unsigned int>(-1170, 6));
00634         waveQueue.push(std::pair<int, unsigned int>(-1203, 6));
00635         waveQueue.push(std::pair<int, unsigned int>(-1236, 6));
00636         waveQueue.push(std::pair<int, unsigned int>(-1269, 6));
