00653             if (enemy->isDead())
00654                 continue;
00655             modBounds = enemy->getBounds();
00656             modBounds.left += modBounds.width / 2 - hitboxSize / 2;
00657             modBounds.top += modBounds.height / 2 - hitboxSize / 2;
00658             modBounds.width = hitboxSize;
00659             modBounds.height = hitboxSize;
00660 
00661             if (bullet->getSizeIndex() == enemy->getSizeIndex() &&
00662                 bulletBounds.intersects(modBounds)
00663             )
00664             {
00665                 player->drawHitmarker();
00666                 bullet->kill();
00667                 enemy->kill();
00668             }
00669         }
00670 
00671         //Player Bullets hitting walls
00672         for (unsigned int i = 0; i < walls.size(); i++)
00673         {
00674             if (!walls.at(i)->checkOnScreen())
00675                 continue;
00676 
00677             for (unsigned int j = 0; j < walls.at(i)->getWallPositions().size();
00678                  j++)
00679             {
00680                 difference = sf::Vector3f
00681                 (abs(walls.at(i)->getWallPositions().at(j).x - 20 - bullet->getPos().x),
00682                     abs(walls.at(i)->getWallPositions().at(j).y + 15 - bullet->getPos().y),
00683                     abs(walls.at(i)->getWallPositions().at(j).z - 10 - bullet->getPos().z));
00684 
00685                 if (difference.x < 25 && difference.y < 20 && difference.z < 20)
00686                     bullet->kill(CharacterBullet::BulletDeathType::WallDeath);
00687             }
00688 
00689             //Player Bullets hit walls build into background
00690             difference.z = abs(
00691                 bullet->getPos().z - walls.at(i)->getWallPositions().at(0).z);
00692 
00693             if (planePos.y > (walls.at(i)->getWallPositions().at(0).y + 10) &&
00694                 difference.z < 10)
00695                 bullet->kill(CharacterBullet::BulletDeathType::WallDeath);
00696         }
00697 
00698         //Player Bullets hitting zap walls
00699         for (unsigned int i = 0; i < zapWalls.size(); i++)
00700         {
00701             if (!zapWalls.at(i)->isOnScreen())
00702                 continue;
00703 
00704             difference = sf::Vector3f
00705             (abs(zapWalls.at(i)->getStartPosition().x - bullet->getPos().x),
00706              abs(zapWalls.at(i)->getStartPosition().y - bullet->getPos().y),
00707              abs(zapWalls.at(i)->getStartPosition().z - bullet->getPos().z));
00708 
00709             if (difference.y < 15 && difference.z < 15)
00710                 bullet->kill(CharacterBullet::BulletDeathType::WallDeath);
00711         }
00712 
00713         //Player Bullets Hitting Boss
00714         if (abs(bullet->getPos().z - boss->getPos().z) <= 10 &&
00715             abs(bullet->getPos().x - boss->getPos().x) < 40 &&
00716             abs(bullet->getPos().y - boss->getPos().y) <= 40)
00717         {
00718             bullet->kill(CharacterBullet::BulletDeathType::WallDeath);
00719 
00720             if (abs(bullet->getPos().z - boss->getPos().z) <= 10 &&
00721                 abs(bullet->getPos().x - boss->getPos().x + 33) <= 10 &&
00722                 abs(bullet->getPos().y - boss->getPos().y + 21) <= 10)
00723                 boss->hit();
00724         }
00725 
00726         //Player bullets hitting boss missile
00727         if (boss->missileCreated() && abs(
00728                 bullet->getPos().z - bossMissile->getPos().z) <= 10 &&
00729             abs(bullet->getPos().x - bossMissile->getPos().x - 50) < 20 &&
00730             abs(bullet->getPos().y - bossMissile->getPos().y) <= 20)
00731         {
00732             bossMissile->damage(1);
00733             bullet->kill(CharacterBullet::BulletDeathType::WallDeath);
00734         }
00735 
00736         //Player bullets hitting boss missile
00737         if (abs(bullet->getPos().z - missile->getPos().z) <= 10 &&
00738             abs(bullet->getPos().x - missile->getPos().x - 50) < 20 &&
00739             abs(bullet->getPos().y - missile->getPos().y) <= 20)
00740         {
00741             missile->damage(1);
00742             bullet->kill(CharacterBullet::BulletDeathType::WallDeath);
00743         }
00744 
00745         bulletNum++;
00746     }
00747 
00748     // boss missile
00749     if (boss->missileCreated() && abs(bossMissile->getPos().z - planePos.z) <=
00750         5)
00751     {
00752         playerDeath();
00753         bossMissile->collide();
00754     }
00755 
00756     // game missile
00757     if (abs(missile->getPos().z - planePos.z) <= 5)
00758     {
00759         playerDeath();
00760         missile->collide();
00761     }
00762 }
00763 
00764 
00772 bool Game::obstacleHit(Obstacle::ObstacleType type, sf::Vector3f difference,
00773                        bool intersect2d)
00774 {
00775     bool hit = true;
00776     sf::Vector3f obstaclePos, playerPos;
00777 
00778     switch (type)
00779     {
00780     case Obstacle::GAS_CAN:
00781     case Obstacle::SATELLITE:
00782     case Obstacle::PLANE:
00783     case Obstacle::SPACE_FUEL:
00784     case Obstacle::GREEN_CANNON_RIGHT:
00785         hit = difference.x < 20 && difference.y < 15 && difference.z < 25;
00786         break;
00787     case Obstacle::GREY_CANNON:
00788     case Obstacle::GREEN_CANNON:
00789         hit = difference.x < 20 && difference.y < 5 && difference.z < 25;
00790         break;
00791     case Obstacle::MISSILE_UP:
00792         hit = intersect2d && difference.y < 8 && difference.z < 8;
00793         break;
00794     }
00795 
00796     return hit;
00797 }
