00810 
00814 void Game::gameOver()
00815 {
00816     gameState = 0;
00817     lives = 3;
00818     selector = 0;
00819     pBackground->setStage(Background::INITIAL);
00820 
00821     // Replace bottom score?
00822     if (currentScores[5] < score)
00823     {
00824         // When replacing and sorting, we need to keep the score and
00825         // initials for that score together for the leaderboard.
00826         currentScores[5] = score;
00827         currentNames[5] = name;
00828 
00829         // Now sort it
00830         for (int i = 0; i < 5; i++)
00831         {
00832             if (currentScores[5 - i] > currentScores[4 - i])
00833             {
00834                 const int tempScore = currentScores[5 - i];
00835                 currentScores[5 - i] = currentScores[4 - i];
00836                 currentScores[4 - i] = tempScore;
00837 
00838                 const std::string tempName = currentNames[5 - i];
00839                 currentNames[5 - i] = currentNames[4 - i];
00840                 currentNames[4 - i] = tempName;
00841             }
00842         }
00843     }
00844 
00845     file.open("ZaxxonScores", std::ios::out);
00846     for (byte i = 0; i < 6; i++)
00847     {
00848         file << currentScores[i] << ' ';
00849 
00850         for (byte j = 0; j < 3; j++)
00851             file << currentNames[i][j];
00852 
00853         file << ' ';
00854     }
00855     file.close();
00856 
00857     // Refresh leaderboard
00858     gui.renderScores(window, currentScores, currentNames);
00859 
00860     // Refresh name
00861     name[0] = '_', name[1] = '_', name[2] = '_';
00862 }
